import{_ as e,c as l,o as n,al as s}from"./chunks/framework.Dem9eySA.js";const y=JSON.parse('{"title":"一个月减肥计划表","description":"","frontmatter":{},"headers":[],"relativePath":"notes/about/vlog/一个月减肥计划表.md","filePath":"notes/about/vlog/一个月减肥计划表.md","lastUpdated":1756901226000}'),g={name:"notes/about/vlog/一个月减肥计划表.md"};function d(r,t,a,o,i,f){return n(),l("div",null,[...t[0]||(t[0]=[s('<h1 id="一个月减肥计划表" tabindex="-1"><strong>一个月减肥计划表</strong> <a class="header-anchor" href="#一个月减肥计划表" aria-label="Permalink to &quot;**一个月减肥计划表**&quot;">​</a></h1><h3 id="每日详细计划" tabindex="-1"><strong>每日详细计划</strong> <a class="header-anchor" href="#每日详细计划" aria-label="Permalink to &quot;**每日详细计划**&quot;">​</a></h3><table tabindex="0"><thead><tr><th style="text-align:left;"><strong>星期</strong></th><th style="text-align:left;"><strong>早餐</strong></th><th style="text-align:left;"><strong>午餐</strong></th><th style="text-align:left;"><strong>晚餐</strong></th><th style="text-align:left;"><strong>加餐</strong></th><th style="text-align:left;"><strong>训练内容</strong></th></tr></thead><tbody><tr><td style="text-align:left;"><strong>周一</strong></td><td style="text-align:left;">水煮蛋 1 个 + 玉米 1 根 + 牛奶 1 杯</td><td style="text-align:left;">清蒸鸡胸肉饭 + 额外蔬菜</td><td style="text-align:left;">鸡胸肉沙拉（少酱）</td><td style="text-align:left;">鸡胸肉 + 苹果 1 个</td><td style="text-align:left;">胸部训练 + 有氧 30 分钟</td></tr><tr><td style="text-align:left;"><strong>周二</strong></td><td style="text-align:left;">水煮蛋 1 个 + 番薯 1 个 + 无糖豆浆 1 杯</td><td style="text-align:left;">清蒸鱼饭 + 额外蔬菜</td><td style="text-align:left;">牛肉沙拉（少酱）</td><td style="text-align:left;">鸡蛋 + 橙子 1 个</td><td style="text-align:left;">手臂训练 + 有氧 30 分钟</td></tr><tr><td style="text-align:left;"><strong>周三</strong></td><td style="text-align:left;">水煮蛋 1 个 + 紫薯 1 个 + 牛奶 1 杯</td><td style="text-align:left;">清蒸鸡胸肉饭 + 额外蔬菜</td><td style="text-align:left;">鸡胸肉沙拉（少酱）</td><td style="text-align:left;">鸡胸肉 + 梨 1 个</td><td style="text-align:left;">肩部训练 + 有氧 30 分钟</td></tr><tr><td style="text-align:left;"><strong>周四</strong></td><td style="text-align:left;">水煮蛋 1 个 + 全麦面包 1 片 + 牛奶 1 杯</td><td style="text-align:left;">清蒸鱼饭 + 额外蔬菜</td><td style="text-align:left;">牛肉沙拉（少酱）</td><td style="text-align:left;">鸡蛋 + 苹果 1 个</td><td style="text-align:left;">背部训练 + 有氧 30 分钟</td></tr><tr><td style="text-align:left;"><strong>周五</strong></td><td style="text-align:left;">水煮蛋 1 个 + 玉米 1 根 + 无糖豆浆 1 杯</td><td style="text-align:left;">清蒸鸡胸肉饭 + 额外蔬菜</td><td style="text-align:left;">鸡胸肉沙拉（少酱）</td><td style="text-align:left;">鸡胸肉 + 橙子 1 个</td><td style="text-align:left;">腿部训练 + 有氧 30 分钟</td></tr><tr><td style="text-align:left;"><strong>周六</strong></td><td style="text-align:left;">水煮蛋 1 个 + 番薯 1 个 + 牛奶 1 杯</td><td style="text-align:left;">清蒸鱼饭 + 额外蔬菜</td><td style="text-align:left;">牛肉沙拉（少酱）</td><td style="text-align:left;">鸡蛋 + 梨 1 个</td><td style="text-align:left;">低强度有氧（快走、骑车等）</td></tr><tr><td style="text-align:left;"><strong>周日</strong></td><td style="text-align:left;">水煮蛋 1 个 + 紫薯 1 个 + 无糖豆浆 1 杯</td><td style="text-align:left;">清蒸鸡胸肉饭 + 额外蔬菜</td><td style="text-align:left;">鸡胸肉沙拉（少酱）</td><td style="text-align:left;">鸡胸肉 + 苹果 1 个</td><td style="text-align:left;">休息或轻松活动（散步、瑜伽等）</td></tr></tbody></table><h3 id="训练内容详细安排" tabindex="-1"><strong>训练内容详细安排</strong> <a class="header-anchor" href="#训练内容详细安排" aria-label="Permalink to &quot;**训练内容详细安排**&quot;">​</a></h3><p>这个计划的特点是<strong>有氧频率更高</strong>，并融入了<strong>高强度间歇训练（HIIT）</strong> 来最大化燃脂效率。</p><table tabindex="0"><thead><tr><th style="text-align:left;">星期</th><th style="text-align:left;">训练主题</th><th style="text-align:left;">无氧训练重点（保证强度，缩短间歇）</th><th style="text-align:left;">有氧/游泳安排（重中之重）</th></tr></thead><tbody><tr><td style="text-align:left;"><strong>周一</strong></td><td style="text-align:left;"><strong>胸部+三头+有氧</strong></td><td style="text-align:left;">卧推、上斜哑铃推举、夹胸、臂屈伸</td><td style="text-align:left;"><strong>训练后：跑步机坡度快走 30-40 分钟</strong></td></tr><tr><td style="text-align:left;"><strong>周二</strong></td><td style="text-align:left;"><strong>背部+二头+有氧</strong></td><td style="text-align:left;">引体向上（或高位下拉）、划船、硬拉</td><td style="text-align:left;"><strong>训练后：游泳 40 分钟</strong>（不间断，中高强度）</td></tr><tr><td style="text-align:left;"><strong>周三</strong></td><td style="text-align:left;"><strong>高强度间歇日</strong></td><td style="text-align:left;"><strong>（无）</strong></td><td style="text-align:left;"><strong>选择一项：</strong> 1. <strong>泳池 HIIT</strong>：快速游 50 米+慢速游 50 米，循环 10-12 组。 2. <strong>跑步机 HIIT</strong>：坡度快跑 1 分钟+慢走 2 分钟，循环 8-10 组。</td></tr><tr><td style="text-align:left;"><strong>周四</strong></td><td style="text-align:left;"><strong>肩部+有氧</strong></td><td style="text-align:left;">肩推、侧平举、面拉</td><td style="text-align:left;"><strong>训练后：椭圆机 30-40 分钟</strong></td></tr><tr><td style="text-align:left;"><strong>周五</strong></td><td style="text-align:left;"><strong>腿部+臀部</strong></td><td style="text-align:left;">深蹲、腿举、腿屈伸、罗马尼亚硬拉</td><td style="text-align:left;"><strong>训练后：慢走 10 分钟放松</strong>（练腿日消耗已很大）</td></tr><tr><td style="text-align:left;"><strong>周六</strong></td><td style="text-align:left;"><strong>长时间有氧日</strong></td><td style="text-align:left;"><strong>（无）</strong></td><td style="text-align:left;"><strong>选择一项：</strong> 1. <strong>游泳 60 分钟</strong>（匀速） 2. <strong>跑步机/椭圆机 60 分钟</strong>（中低强度）</td></tr><tr><td style="text-align:left;"><strong>周日</strong></td><td style="text-align:left;"><strong>休息或积极恢复</strong></td><td style="text-align:left;"><strong>（无）</strong></td><td style="text-align:left;"><strong>彻底休息，或进行 30 分钟轻度活动</strong>（如散步、瑜伽）</td></tr></tbody></table>',6)])])}const h=e(g,[["render",d]]);export{y as __pageData,h as default};
